```ruby:brainfuck.rb
class Tape
  def initialize
    @position = 0
    @tape = [0]
  end

  def get
    @tape[@position]
  end

  def set(val)
    @tape[@position] = val 
  end

  def inc
    @tape[@position] += 1
  end

  def dec
    @tape[@position] -= 1
  end

  def advance
    @position += 1
    @tape.push(0) if @tape.size <= @position
  end

  def devance
    @position -= 1
  end
end

def mainloop(program, map)
  pc = 0
  tape = Tape.new

  while (pc < program.size)
    c = program[pc]
    case c
    when '+'
      tape.inc
    when '-'
      tape.dec
    when '>'
      tape.advance
    when '<'
      tape.devance
    when '['
      pc = map[pc] if tape.get == 0
    when ']'
      pc = map[pc] if tape.get != 0
    when '.'
      print tape.get.chr
    when ','
    end
    pc += 1
  end
end

def parse(program)
  parsed = []
  map = {}
  stack = []
  keywords = ['[',']','<','>','+','-',',','.']

  pc = 0
  program.each_byte do |c|
    c = c.chr
    next unless keywords.include?(c)
    parsed.push c
    if c == '['
      stack.push pc
    elsif c == ']'
      left = stack.pop
      right = pc
      map[left] = right
      map[right] = left
    end
    pc += 1
  end

  return parsed.join(''), map
end

def run(input)
  program, map = parse(input)
  mainloop(program, map)
end

open(ARGV[0]) do |f|
  run(f.read)
end

